mapper.select:
select count(*) 
from f_folder 
where p_no = #{p_no}

select * 
from f_folder 
where folder_num = (select min(folder_num)
					from f_folder 
					where p_no = #{p_no}
				 	)

mapper.insert:
select nvl(max(folder_num),0)+1 from f_folder
insert into f_folder (p_no, folder_num, folder_name, folder_path) values (#{p_no}, #{folder_num},
'HOME', #{p_no}||'/'
#{folder_name}, #{folder_path}
)

mapper.update:
update f_folder 
set folder_name = #{name} 
where folder_num = #{num}
