mapper.update:
update Reservation 
set purpose = #{purpose}, names = #{names}, day = #{day},
	start_time = #{start_time}, end_time = #{end_time}  
where num = #{num}

mapper.select:
select *
from ( select rownum rnum, b.*
	 from ( select * 
	        from Reservation 
	   		where id = #{id} 
	   		
	   		and day between #{start_day} and #{end_day}
	   		
	   		order by day
	 	  )b 
	 where rownum <= #{end} 
	 ) 
where rnum between #{start} and #{end}

select count(*)
from Reservation 
where id = #{id} 

and day between #{start_day} and #{end_day}

order by day
